
public class SwitchTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 4;

		switch (a) {
		case 0:
			System.out.println("a单0");
			break;
		case 1:
			System.out.println("a单1");
			break;
		case 2:
			System.out.println("a单2");
			break;
		default:
			System.out.println("aぃ单0,1,2");

		}
		System.out.println("after switch");

	}

}
